import axios from "axios";
import { createContext } from "react";
import React, { useEffect, useState } from "react";

export const listContext = createContext();

export function ListContextProvider(props) {
  const [listId, setListId] = useState(null);
//   const [numberOfCartItems, setNumberOfCartItems] = useState(0);

  let headers = {
    token: localStorage.getItem("userToken"),
  };
  function addToList(id) {
    return axios
      .post(
        "https://ecommerce.routemisr.com/api/v1/wishlist",
        {
          productId: id,
        },
        {
          headers,
        }
      )
      .then((response) => response)
      .catch((error) => error);
  }
//   function updateListQuantity(count, id) {
//     return axios
//       .put(
//         `https://ecommerce.routemisr.com/api/v1//${id}`,
//         {
//           count,
//         },
//         {
//           headers,
//         }
//       )
//       .then((response) => response)
//       .catch((error) => error);
//   }
  function deleteListItem(productId) {
    return axios
      .delete(`https://ecommerce.routemisr.com/api/v1/wishlist/${productId}`, {
        headers,
      })
      .then((response) => response)
      .catch((error) => error);
  }
  function getLoggedList() {
    return axios
      .get("https://ecommerce.routemisr.com/api/v1/wishlist", {
        headers,
      })
      .then((response) => response)
      .catch((error) => error);
  }
  async function getData() {
    const { data } = await getLoggedList();
    // setNumberOfCartItems(data.numOfCartItems)
    console.log(data);
    setcartId(data.data._id);
  }
  useEffect(() => {
    getData();
    
  }, []);
//   function OnlinePayment(cartId, shippingAddress) {
//     return axios
//       .post(
//         `https://ecommerce.routemisr.com/api/v1/orders/checkout-session/${cartId}?url=http://localhost:3000`,
//         {
//           shippingAddress,
//         },
//         {
//           headers,
//         }
//       )
//       .then((response) => response)
//       .catch((error) => error);
//   }

  return (
    <ListContextProvider.Provider
      value={{
        addToList,
        getLoggedList,
        deleteListItem,
        // updateCartQuantity,
        // OnlinePayment,
        listId,
        // numberOfCartItems,
        // setNumberOfCartItems,
      }}
    >
      {props.children}
    </ListContextProvider.Provider>
  );
}
