import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { useQuery } from "react-query";
import { MagnifyingGlass } from "react-loader-spinner";
import { Link } from "react-router-dom";
function FeaturedProduct() {
  // const [Products, setProducts] = useState([]);
  // async function getAllProducts() {
  //   let { data } = await axios.get(
  //     "https://ecommerce.routemisr.com/api/v1/products"
  //   );
  //   setProducts(data.data);
  // }

  // useEffect(() => {
  //   getAllProducts();
  // });


  const {addToCart}=useContext
  function getData() {
    return axios.get("https://ecommerce.routemisr.com/api/v1/products");
  }
  let { isLoading, isError, data } = useQuery("featuredProduct", getData);

  return (
    <div className="row">
      {data?.data.data.length > 0 ? (
        data?.data.data.map((product) => (
          <div className="col-md-2" key={product._id}>
            <Link className="text-decoration-none" to={`/ProductDetails/${product._id}`}>
          <div className="inner product p-3 rounded text-black">
            <img
              src={product.imageCover}
              className="w-100"
              alt={product.title}
            ></img>
            <h4 className="h6 my-2">{product.category.name}</h4>
            <h3 className="h6 fw-bold">
              {product.title.split(" ", 2).join(" ")}
            </h3>
            <div className="d-flex justify-content-between p-2">
              <small className="col-6 ">{product.price} EGP</small>
              <div className="col-6 px-1">
                <i className="fa-solid fa-star"></i>
                {product.ratingsAverage}
              </div>
            </div>
          </div>
          </Link>
          <button className="btn btn-success w-100">ADD TO CART</button>
        </div>
        ))
      ) : (
        <div className="d-flex w-100 justify-content-center">
          <MagnifyingGlass
            visible={true}
            height="80"
            width="80"
            ariaLabel="MagnifyingGlass-loading"
            wrapperStyle={{}}
            wrapperClass="MagnifyingGlass-wrapper"
            glassColor="#c0cfff"
            color="#e15b64"
          />
        </div>
      )}
    </div>
  );
}

export default FeaturedProduct;
