import React, { useContext, useEffect, useState,  } from "react";
import { Link} from "react-router-dom";
import emptyCart from "../../assets/images/empty cart.png";
import { ListContext } from "../../Context/WishListContext";

const List = () => {
  const { getLoggedList,deleteListItem} =
    useContext(ListContext);
  const [data, setData] = useState({});


  async function deleteItem(id) {
    const { data } = await deleteListItem(id);
    console.log(data);
    setData(data);
    // setNumberOfCartItems(data.numOfCartItems)
  }

  async function getData() {
    let { data } = await getLoggedList();
    console.log(data);
    setData(data);
  }
  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="p-3 ">
      <h2>Wish List</h2>
      {console.log(data.data)}
      {data.length > 0 ? (
          brands.map((product) => (
            
            <div
              className="row align-items-center gy-2 border-bottom py-3"
              key={product.product._id}
            >
              <div className="col-10">
                <div className="row align-items-center">
                  <div className="col-2">
                    <img
                      src={product.product.imageCover}
                      className="w-100 mb-2"
                      alt={product.product.title}
                    />
                  </div>
                  <div className="col-10 mb-2">
                    <h6>{product?.product?.title}</h6>
                    <p>{product.price} EGP</p>
                    <p className="cursor-pointer">
                      <i
                        className="fa-solid fa-trash mx-2 "
                        onClick={() => {
                          deleteItem(product.product.id);
                        }}
                      ></i>
                      Remove
                    </p>
                  </div>
                </div>
              </div>
              {/* <div className="col-2 d-flex align-items-center">
                <button
                  className="btn-sm"
                  onClick={() => {
                    updateCount(product.count + 1, product.product.id);
                  }}
                >
                  +
                </button>
                <p className="mx-2 mb-0">{product.count}</p>
                <button
                  className="btn-sm"
                  onClick={() => {
                    updateCount(product.count - 1, product.product.id);
                  }}
                >
                  -
                </button>
              </div> */}
            </div>
          ))
        ) : (
          <div className="d-flex w-100 justify-content-center">
            <MagnifyingGlass
              visible={true}
              height="80"
              width="80"
              ariaLabel="MagnifyingGlass-loading"
              wrapperStyle={{}}
              wrapperClass="MagnifyingGlass-wrapper"
              glassColor="#c0cfff"
              color="#e15b64"
            />
          </div>
        )}
    </div>
  );
};
export default List;
