import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../../Context/CartContext";

const Cart = () => {
  const { getLoggedCart, deleteCartItem,updateCartQuantity } = useContext(CartContext);
  const [data, setData] = useState({});



  async function updateCount(count,id) {
    const { data } = await updateCartQuantity(count,id);
    setData(data);
  }
  async function deleteItem(id) {
    const { data } = await deleteCartItem(id);
    setData(data);
  }

  async function getData() {
    let { data } = await getLoggedCart();
    // console.log(data);
    setData(data);
  }
  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="p-3 ">
      <h2>Shop Cart</h2>
      <p>Total Cart Price : {data?.data?.totalCartPrice} EGP</p>

      {data?.data?.products.map((product) => (
        <div className="row align-items-center gy-2 border-bottom py-3">
          <div className="col-10">
            <div className="row align-items-center">
              <div className="col-2">
                <img
                  src={product.product.imageCover}
                  className="w-100 mb-2"
                  alt={product.product.title}
                />
              </div>
              <div className="col-10 mb-2">
                <h6>{product.product.title}</h6>
                <p>{product.price} EGP</p>
                <p className="cursor-pointer">
                  <i
                    className="fa-solid fa-trash mx-2 "
                    onClick={() => {
                      deleteItem(product.product.id);
                    }}
                  ></i>
                  Remove
                </p>
              </div>
            </div>
          </div>
          <div className="col-2 d-flex align-items-center">
            <button className="btn-sm"onClick={() => {
                      updateCount(product.count,product.product.id);
                    }}>+</button>
            <p className="mx-2 mb-0">{product.count}</p>
            <button className="btn-sm">-</button>
          </div>
        </div>
      ))}
    </div>
  );
};
export default Cart;
