  async function getAllCategories() {
    let { data } = await axios.get(
      "https://ecommerce.routemisr.com/api/v1/categories"
    );
    console.log(data.data)
    // setProducts(data.data);
  }

  useEffect(() => {
    getAllCategories();
  });
  return (
    <div>
